export async function loadPage(a){const n=document.querySelector("main.container");try{const t=await fetch(a),i=(await t.text()).match(/<main[^>]*>([\s\S]*?)<\/main>/i);if(!i)return void(n.innerHTML="<p>Erro: página sem tag <main>.</p>");const r=i[1];n.innerHTML=r,a.includes("cadastro.html")&&import("../css/formValidation.js").then((a=>{a.initFormValidation()})).catch((a=>{}))}catch(a){n.innerHTML="<p>Erro ao carregar página.</p>"}}